      font-size: 1.3rem;
      margin-bottom: 1rem;
    }

    .options {
      display: flex;
      flex-direction: column;
      margin-bottom: 1rem;
    }

    .nav-buttons {
      display: flex;
      justify-content: center;
    }

    .score-display {
      font-size: 1.2rem;
      font-weight: bold;
      margin-top: 2rem;
    }
  </style>
</head>
<body>
  <div class="container">
    <div class="question"></div>
    <div class="options"></div>
    <div class="nav-buttons">
      <button onclick="prevQuestion()">Previous</button>
      <button onclick="nextQuestion()">Next</button>
    </div>
    <div class="score-display"></div>
  </div>

  <script>
    const questions = [
      {
        question: "Scenario 1: ...",
        options: ["Option 1", "Option 2", "Option 3", "Option 4"],
        answer: 1,
      },
      {
        question: "Scenario 2: ...",
        options: ["Option 1", "Option 2", "Option 3", "Option 4"],
        answer: 2,
      },
      {
        question: "Scenario 3: ...",
        options: ["Option 1", "Option 2", "Option 3", "Option 4"],
        answer: 3,
      },
    ];

    let currentQuestion = 0;
    let score = 0;

    function displayQuestion() {
      const questionElem = document.querySelector('.question');
      const optionsElem = document.querySelector('.options');
      optionsElem.innerHTML = '';

      questionElem.textContent = questions[currentQuestion].question;

      questions[currentQuestion].options.forEach((option, index) => {
        const input = document.createElement('input');
        input.type = 'radio';
        input.name = 'option';
        input.value = index;
        input.id = `option${index}`;
        input.onclick = checkAnswer;
        const label = document.createElement('label');
        label.htmlFor = `option${index}`;
        label.innerText = option;

        const div = document.createElement('div');
        div.appendChild(input);
        div.appendChild(label);

        optionsElem.appendChild(div);
      });
    }

    function checkAnswer() {
      const selectedOption = Number(document.querySelector('input[name="option"]:checked').value);
      const correctAnswer = questions[currentQuestion].answer;

      if (selectedOption === correctAnswer) {
        score++;
      }

      displayScore();
      nextQuestion();
    }

    function displayScore() {
      document.querySelector('.score-display').textContent = `Score: ${score}`;
    }

    function nextQuestion() {
      currentQuestion++;
      if (currentQuestion >= questions.length) {
        currentQuestion = 0;
      }
      displayQuestion();
    }

    function prevQuestion() {
      currentQuestion--;
      if (currentQuestion < 0) {
        currentQuestion = questions.length - 1;
      }
      displayQuestion();
    }

    displayQuestion();
  </script>
</body>
</html>
