```javascript
// Validate and format credit card numbers
function validateAndFormatCardNumber(cardNumber) {
  // Check if the number is a valid credit card number
  if (!luhnCheck(cardNumber)) {
    return false;
  }

  // Remove any non-numeric characters
  cardNumber = cardNumber.replace(/[^\d]/g, '');

  // Format the number with groups of four digits separated by commas
  let formattedCardNumber = '';
  while (cardNumber.length > 0) {
    formattedCardNumber += cardNumber.slice(-4) + ',';
    cardNumber = cardNumber.slice(0, -4);
  }
  formattedCardNumber += cardNumber;

  return formattedCardNumber;
}

// Check if a given number is a valid credit card number using the Luhn algorithm
function luhnCheck(cardNumber) {
  let sum = 0;
  let alternative = false;
  for (let i = cardNumber.length - 1; i >= 0; i--) {
    const digit = cardNumber[i];
    sum += (+digit) * (alternative ? 1 : 2);
    alternative = !alternative;
  }

  return sum % 10 === 0;
}

