void ModbusWriteCoil(uint8_t address, uint16_t coil, bool state)
{
    uint8_t txBuffer[8];
    uint8_t coilHigh, coilLow, valueHigh, valueLow;
    uint16_t crcValue;
    uint8_t crcHigh, crcLow;

    SplitInt16(coil, &coilHigh, &coilLow);
