  let mostSimilarSign = null;

  for (const sign in comparison) {
    const signData = comparison[sign];
    const similarity = handpose.signsSimilarity(handData, signData, lambda);

    if (similarity > maxSimilarity) {
      maxSimilarity = similarity;
      mostSimilarSign = sign;
    }
  }

  return mostSimilarSign;
}
