        this.apiUrl = userDetails.getApiUrl();
        this.apiKeyHeader = userDetails.getApiKeyHeader();

        this.clientBuilder = WebClient.builder();
        this.dataSource = new DriverManagerDataSource();
    }

    public Uni<ResponseEntity<List<MyApiResponse>>> getMyApIResponses() {
        return this.clientBuilder
                .build()
                .get()
                .uri(this.apiUrl)
                .retrieve()
                .bodyToFlux(MyApiResponse.class)
                .filter((myApiResponse) -> {
                    if (myApiResponse.getStatus().equals("accepted")) {
                        return true;
                    } else {
                        return false;
                    }
                })
                .collectList()
                .onErrorMap((error) -> Mono.error(APIServiceException.createWith(error)))
                .flatMapMany((myApiResponses) ->
                        Flux.fromIterable(myApiResponses)
                                .map((myApiResponse) -> {
                                    // Save the response to a new table here
                                    return ResponseEntity.ok(myApiResponse);
                                })
                )
                .collect()
                .flatMap(Collectors.toList())
                .cast(ResponseEntity.class);
    }
}
```
