function base62Encode($num) {
    $base62Chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    $encoded = '';

    while ($num > 0) {
        $encoded = $base62Chars[$num % 62] . $encoded;
        $num = floor($num / 62);
    }

    return $encoded;
}
