```kotlin
import android.bluetooth.BluetoothAdapter
import android.bluetooth.BluetoothDevice
import android.bluetooth.BluetoothGatt
import android.bluetooth.BluetoothGattCallback
import android.bluetooth.BluetoothProfile
import android.content.Context
import android.content.Intent
import android.content.pm.PackageManager
import androidx.core.content.ContextCompat

class BluetoothManager(private val context: Context, private val gattCallback: BluetoothGattCallback) {

    private val bluetoothAdapter: BluetoothAdapter? by lazy(LazyThreadSafetyMode.NONE) {
        val bluetoothManager = context.getSystemService(Context.BLUETOOTH_SERVICE) as android.bluetooth.BluetoothManager
        bluetoothManager.adapter
    }

    fun isBluetoothSupported() = bluetoothAdapter != null
    fun isBluetoothEnabled() = bluetoothAdapter?.isEnabled == true
    fun isLocationPermissionGranted() = ContextCompat.checkSelfPermission(context, android.Manifest.permission.ACCESS_FINE_LOCATION) == PackageManager.PERMISSION_GRANTED

    fun startScanning(onDeviceFound: (BluetoothDevice) -> Unit) {
        val scanCallback = object : BluetoothAdapter.LeScanCallback {
            override fun onLeScan(device: BluetoothDevice?, rssi: Int, scanRecord: ByteArray?) {
                device?.let {
                    onDeviceFound(it)
                }
            }
        }
        bluetoothAdapter?.startDiscovery()
        bluetoothAdapter?.startLeScan(scanCallback)
    }

    fun connect(device: BluetoothDevice) {
        device.connectGatt(context, false, gattCallback)
    }

     fun disconnect(gatt: BluetoothGatt) {
        gatt.disconnect()
        gatt.close()
    }
}
